import { Component } from '@angular/core';
import { GameResult } from '../models/game-result.model';
import { GameService } from '../game.service';

@Component({
  selector: 'app-chifoumi',
  templateUrl: './chifoumi.component.html',
  styleUrls: ['./chifoumi.component.css']
})
export class ChifoumiComponent {
    constructor(private gameService:GameService) {   

    }
    computerChoice? :  'Ciseaux'| 'Pierre' | 'Feuille';
    showResultMessage = false;
    resultOfTheGame?: 'Vous avez gagné'|"Vous avez perdu"|'Match nul';
    resultMessage?:string;
    

    play(playerChoice: 'Ciseaux'|'Pierre'|'Feuille') {
        this.computerChoice =Math.random() > 0.33 ? 'Ciseaux' : Math.random()>0.50 ? 'Pierre' : 'Feuille';

        this.resultOfTheGame = Math.random() > 0.33 ? "Vous avez gagné" : Math.random()>0.50 ? "Vous avez perdu" : 'Match nul';
        
        this.showResultMessage = true;

        const gameResult: GameResult = {
            playerChoice: playerChoice,
            computerChoice: this.computerChoice ,
            result: this.resultOfTheGame,
            timestamp: new Date()
          };
        
        this.gameService.playGame(gameResult).subscribe(response => {
            console.log(response);
        });
    }

    hideResult() {
        this.showResultMessage = false;
    }
}
